<script type="text/javascript">
   function allowDrop(ev) {
       ev.preventDefault();
   }

   function drag(ev) {
       ev.dataTransfer.setData("text", ev.target.id);

   }

//window.addEventListener('DOMContentLoaded', plsWork, false);
// ^ autorefresher

   function plsWork()
{
if(localStorage.getItem("refresh") !== "yes"){
window.location.reload();
localStorage.setItem("refresh","yes");
}
else
localStorage.removeItem("refresh");
}

   function drop(ev) {
       ev.preventDefault();
       var data = ev.dataTransfer.getData("text");
       console.log(data);
       var comisie='';
       ev.target.parentElement.insertBefore(document.getElementById(data),ev.target.nextSibling);
       document.getElementById('i'+data).value = ev.target.id.substr(1,2);
       // inserts after the target
       }
</script>



