require(['jquery'], function($) {
  $(document).ready(function(){
    $("#getDay").click(function(){

       var weekday = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
       var d = new Date(document.getElementById('enrollmentWeekStart').value);
       var day = d.getDay();

       if (day == 0){
          var result = 7;
       }else{
          var result = 7 - day;
       }

      var finalDate = d.getDate() + result;
      if (finalDate < 10){
        finalDate = '0' + finalDate;
      }

      var month = d.getMonth() + 1;
      if (month < 10) {
        month = '0' + month;
      }

      var final = d.getFullYear() + '-' + month + '-' + finalDate;
       document.getElementById('enrollmentWeekEnd').value = final;

      console.log(final);



